// 防止utils.js中的btf重复声明
window.btfInitialized = false;

// 页面加载优化：延迟加载非关键资源
document.addEventListener('DOMContentLoaded', function() {
    // 1秒后加载非关键资源
    setTimeout(function() {
        // 懒加载图片初始化
        if (typeof window.lazyLoadInstance === 'undefined' && typeof LazyLoad === 'function') {
            window.lazyLoadInstance = new LazyLoad({
                elements_selector: 'img[data-lazy-src],.lazy',
                threshold: 300,
                callback_error: function(img) {
                    img.setAttribute('src', img.getAttribute('data-error-src') || 'https://dddd.ct8.pl/usr/themes/butterfly/img/404.jpg');
                    img.classList.add('error');
                }
            });
        }

        // 延迟加载其他非关键脚本
        [
            'https://dddd.ct8.pl/usr/themes/butterfly/js/snackbar.min.js',
            'https://dddd.ct8.pl/usr/themes/butterfly/js/rightside.js'
        ].forEach(function(src) {
            if (src) {
                var script = document.createElement('script');
                script.src = src;
                script.defer = true;
                document.body.appendChild(script);
            }
        });
    }, 1000);
});

nav.script:
// 设置明/暗模式切换按钮的初始状态和点击事件
const firstMode = document.documentElement.getAttribute('data-theme') === 'dark' ? 'dark' : 'light';
let spanElement = document.querySelector('.dark-light-com span');
if (firstMode === 'light') {
    spanElement.textContent = 'Light';
} else {
    spanElement.textContent = 'Dark';
}
document.querySelector('.dark-light-com').addEventListener('click', function() {
    const nowMode = document.documentElement.getAttribute('data-theme') === 'dark' ? 'dark' : 'light';
    let spanElement = this.querySelector('span');
    if (nowMode === 'light') {
        spanElement.textContent = 'Dark';
        activateDarkMode();
        saveToLocal.set('theme', 'dark', 2);
        GLOBAL_CONFIG.Snackbar !== undefined && btf.snackbarShow(GLOBAL_CONFIG.Snackbar.day_to_night);
    } else {
        spanElement.textContent = 'Light';
        activateLightMode();
        saveToLocal.set('theme', 'light', 2);
        GLOBAL_CONFIG.Snackbar !== undefined && btf.snackbarShow(GLOBAL_CONFIG.Snackbar.night_to_day);
    }
    // handle some cases
    typeof utterancesTheme === 'function' && utterancesTheme();
    typeof changeGiscusTheme === 'function' && changeGiscusTheme();
    typeof FB === 'object' && window.loadFBComment();
    window.DISQUS && document.getElementById('disqus_thread').children.length && setTimeout(() => window.disqusReset(), 200);
    typeof runMermaid === 'function' && window.runMermaid();
});

div.script:
$(document).ready(function() {
  var a = document.getElementsByTagName("a");
  for (let i = 0; i < a.length; i++) {
    let domain = document.domain;
    let url = a[i].href;
    if (typeof(url) != "undefined" && url.length != 0 && url.match(domain) == null && url != "javascript:void(0);") {
      a[i].setAttribute("target", "_BLANK")
    }
  }
});
